import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { Button, StyleSheet, Text, View, Image } from 'react-native';

import logo from './assets/cao_e_gato_capa.png';

export default function App() {
  const [texto1, setTexto1] = useState('Seja bem vendx ao App CuidaBicho!');

  return (
    <View style={styles.container1}>
      <View>
        <Image style={styles.imagem} source={logo}></Image>
        <View style={styles.container2}>
          <Button onPress={() => {setTexto1('Cadastro')}} title='Cadastre o Bichano'></Button>
          <Text style={styles.titulo1}>{texto1}</Text>
          <Text style={styles.texto_normal}> Nosso objetivo é construir uma rede colaborativa de amigos dos bichamos. Medicamentos que seu bichinho não irá mais precisar ou outros materiais que possam ser úteis para outras famílias. </Text>
        </View>
      </View> 
    </View>
  );
}

const styles = StyleSheet.create({
  container1: {
    flex: 1,
    backgroundColor: '#yellow',
    alignItems: 'center',
  },
  container2: {
    flexDirection: 'row',
    marginVertical: 10,
    alignItems: 'center',
  },
  texto_normal: {
    fontSize: 14 ,
    justifyContent: 'center',
    color: '#444',
    marginVertical: 10,
    marginHorizontal: 20,
    textAlign: 'justify',
  },
  titulo1: {
    fontSize: 18 ,
    fontWeight: 'bold',
    alignContent: 'center',
    justifyContent: 'center',
    marginVertical: 20,
  },
  imagem:{
    marginTop: 50,
justifyContent: 'center',
    width: 100,
    height: 100,

  },
});
