import React, {useState} from 'react';
import { Button, StyleSheet, Text, View, Image } from 'react-native';

//import logo from '../assets/cao_e_gato_capa.png';

export default function Card(props) {
  const [texto1, setTexto1] = useState('Seja bem vendx ao App CuidaBicho!');
  return (
      <View style={styles.container2}>
        <Image style={styles.imagem} source={{uri: props.img}}></Image>
        <View style={styles.container3}>
          <Text style={styles.titulo1} >{props.nome}</Text>
          <Text style={styles.texto_normal} onPress={()=>{setTexto1('Seja bem vindx ao App CuidaBicho')}}> {texto1} </Text>
          <Button onPress={() => {setTexto1('Cadastro')}} title={props.botao}></Button>
        </View>
      </View> 
  );
}

const styles = StyleSheet.create({
  container2: {
    flexDirection: 'row',
    backgroundColor: 'gray',
    marginVertical: 10,
    alignItems: 'center',
    padding: 10,
  },
  container3: {
    flex: 1,
    backgroundColor: 'pink',
    marginBottom: 5,
    borderWidth: 10,
    borderColor: 'pink',
  },
  texto_normal: {
    fontSize: 14 ,
    color: '#444',
    textAlign: 'justify',
  },
  titulo1: {
    fontSize: 14 ,
    fontWeight: 'bold',
  },
  imagem:{
    marginRight: 10,
    justifyContent: 'center',
    width: 80,
    height: 80,

  },
});
