import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { Button, StyleSheet, Text, View, Image } from 'react-native';

import logo from './assets/cao_e_gato_capa.png';

export default function App() {
  const [texto1, setTexto1] = useState('Seja bem vendx ao App CuidaBicho!');

  return (
     <View style={styles.container1}>
      <StatusBar style="auto" />
      <View style={styles.container2}>
        <Image style={styles.imagem} source={logo}></Image>
        <View style={styles.container3}>
          <Text style={styles.titulo1}>{texto1}</Text>
          <Text style={styles.texto_normal}> Descrição. </Text>
          <Button onPress={() => {setTexto1('Cadastro')}} title='Cadastre o Bichano'></Button>
        </View>
      </View> 
    </View>
  );
}

const styles = StyleSheet.create({
  container1: {
    flex: 1,
    backgroundColor: 'yellow',
    alignItems: 'center',
    paddingTop: 20,
  },
  container2: {
    flexDirection: 'row',
    backgroundColor: 'gray',
    marginVertical: 10,
    alignItems: 'center',
    padding: 10,
  },
  container3: {
    flex: 1,
    backgroundColor: 'pink',
    alignItems: 'center',
    marginBottom: 5,
  },
  texto_normal: {
    fontSize: 14 ,
    justifyContent: 'center',
    color: '#444',
    textAlign: 'justify',
  },
  titulo1: {
    fontSize: 14 ,
    fontWeight: 'bold',
    alignContent: 'center',
    justifyContent: 'center',
  },
  imagem:{
    marginRight: 10,
    justifyContent: 'center',
    width: 50,
    height: 50,

  },
});
